import styled from "styled-components";

export const Container = styled.div`
  position: relative;
`;

export const Button = styled.button`
  padding: 5px;
  display: flex;
  justify-content: center;
  align-items: center;

  &.btn-pressed {
    border-radius: 50%;
    background: ${(props) => props.theme.options.bg};
  }
`;

export const Options = styled.ul`
  z-index: 1000;
  top: 40px;
  right: 0;
  position: absolute;
  width: 200px;
  max-height: 400px;
  padding: 10px 0;
  background-color: ${(props) => props.theme.common.secondaryColor};
  border-radius: 3px;
  box-shadow: ${(props) => props.theme.options.boxShadow};
  transition: all 0.3s ease;
  overflow-y: auto;
  transform: translateX(100%);

  &.active {
    transform: translateX(0);
  }

  &.right {
    right: unset;
    left: -210px;
  }
`;

export const Option = styled.li`
  padding: 15px 20px;
  color: ${(props) => props.theme.common.mainHeadingColor};
  font-size: 0.9rem;
  cursor: pointer;

  &:hover {
    background: ${(props) => props.theme.common.primaryColor};
  }
`;

export const Profile = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 10px;
  border-bottom: 1px solid ${(props) => props.theme.common.borderColor};
`;

export const ImageWrapper = styled.div`
  margin-right: 10px;
`;

export const Avatar = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
`;

export const Actions = styled.div`
  display: flex;
  align-items: center;
`;

export const ThemeIconContainer = styled.div`
  cursor: pointer;
  margin-left: 10px;
`;
